mapper.select:
SELECT "CUSTOM_ANALYSIS_TEMPLATE_REQUEST_SEQUENCE_PK" AS "id" ,"NAME" AS "name" ,"DATA_SUMMARY" AS "entities" ,"DATA_STARTDATE" AS "dataStartDate" ,"DATA_ENDDATE" AS "dataEndDate" ,"PROGRESS_STATE" AS "progressState" , to_char ("PROGRESS_CHANGE_DATETIME"::timestamp at time zone 'KST', 'YYYY-MM-DD"T"HH24:MI:SS,MSOF:00"') as "progressChangedAt" ,"ADMIN_COMMENT" AS "adminComment" , to_char ("CREATE_DATETIME"::timestamp at time zone 'KST', 'YYYY-MM-DD"T"HH24:MI:SS,MSOF:00"') as "createdAt" , to_char ("MODIFY_DATETIME"::timestamp at time zone 'KST', 'YYYY-MM-DD"T"HH24:MI:SS,MSOF:00"') as "modifiedAt" ,"USER_CANCEL_DATETIME" AS "userCancelDatetime" ,"USER_ID" AS "creatorId" FROM "CUSTOM_ANALYSIS_TEMPLATE_REQUEST" WHERE "PROGRESS_STATE" in ('ongoing', 'standby','reject')
AND "USER_ID" = #{userId}
ORDER BY "CREATE_DATETIME" DESC
