mapper.select:
SELECT * , to_char(bsReq."CREATE_DATETIME", 'YYYY-MM-DD') as "createDataTime" , (SELECT "NAME" FROM "ORIGINAL_DATA" WHERE "INSTANCE_SEQUENCE_FK2" = bsReq."INSTANCE_SEQUENCE_FK2" AND "ORIGINAL_DATA_SEQUENCE_PK" = (SELECT "ORIGINAL_DATA_SEQUENCE_FK1" FROM "MODEL" WHERE "INSTANCE_SEQUENCE_FK3"= bsReq."INSTANCE_SEQUENCE_FK2" AND "MODEL_SEQUENCE_PK" = bsReq."MODEL_SEQUENCE_FK1" ) ) AS "dataName" , (SELECT "NAME" FROM "PROJECT" WHERE "SELECTED_INSTANCE" =bsReq."INSTANCE_SEQUENCE_FK2" LIMIT 1) AS "projectName" , (SELECT "NAME" FROM "MODEL" WHERE "INSTANCE_SEQUENCE_FK3"= bsReq."INSTANCE_SEQUENCE_FK2" AND "MODEL_SEQUENCE_PK" = bsReq."MODEL_SEQUENCE_FK1" ) AS "modelName" FROM "BATCH_SERVICE_REQUEST" AS bsReq WHERE bsReq."DELETE_FLAG" = FALSE
AND bsReq."USER_ID" = #{userId}
ORDER BY bsReq."CREATE_DATETIME" DESC

SELECT * , to_char("CREATE_DATETIME", 'YYYY-MM-DD') as "createDataTime" , (SELECT "NAME" FROM "INSTANCE" WHERE "INSTANCE_SEQUENCE_PK" =bs."BATCH_INSTANCE_SEQUENCE_FK2") AS "batchServer" , (SELECT "NAME" FROM "PROJECT" WHERE "SELECTED_INSTANCE" =bs."SANDBOX_INSTANCE_SEQUENCE_FK1" LIMIT 1) AS "projectName" , (SELECT "NAME" FROM "ORIGINAL_DATA" WHERE "INSTANCE_SEQUENCE_FK2" = bs."SANDBOX_INSTANCE_SEQUENCE_FK1" AND "ORIGINAL_DATA_SEQUENCE_PK" = (SELECT "ORIGINAL_DATA_SEQUENCE_FK1" FROM "MODEL" WHERE "INSTANCE_SEQUENCE_FK3"= bs."SANDBOX_INSTANCE_SEQUENCE_FK1" AND "MODEL_SEQUENCE_PK" = bs."MODEL_SEQUENCE_FK4" ) ) AS "dataName" , (SELECT "NAME" FROM "MODEL" WHERE "INSTANCE_SEQUENCE_FK3"= bs."SANDBOX_INSTANCE_SEQUENCE_FK1" AND "MODEL_SEQUENCE_PK" = bs."MODEL_SEQUENCE_FK4" ) AS "modelName" FROM "BATCH_SERVICE" AS bs WHERE "DELETE_FLAG" = FALSE
AND "ENROLLEMENT_ID" = #{userId}
ORDER BY "CREATE_DATETIME" DESC

SELECT lg."LOG_BATCH_SEQUENCE_PK" , COALESCE((SELECT "NAME" FROM "BATCH_SERVICE" WHERE "BATCH_SERVICE_SEQUENCE_PK" = lg."BATCH_SERVICE_SEQUENCE_FK3"),'') AS "batchName" , cd."CODE_NAME" AS codeName , abstime(lg."BATCH_START_DATETIME") as "batchStartDateTime" , abstime(lg."BATCH_END_DATETIME") as "batchEndDateTime" , lg."BATCH_IS_SUCCESS" , abstime(lg."CREATE_DATETIME") as "createDataTime" , lg."LOG_BATCH_SEQUENCE_PK" AS "detail" FROM (SELECT * FROM "LOG_BATCH" WHERE "CREATE_DATETIME" BETWEEN to_timestamp(#{startDate}, 'YYYY-MM-DD') AND (to_timestamp( #{endDate}, 'YYYY-MM-DD')) + interval '1' day ) AS lg INNER JOIN "CODE" AS cd ON lg."CODE" = cd."CODE" WHERE 1=1
AND lg."BATCH_SERVICE_SEQUENCE_FK3" IN ( SELECT "BATCH_SERVICE_SEQUENCE_PK" FROM "BATCH_SERVICE" WHERE "ENROLLEMENT_ID" = #{userId} )
ORDER BY "${sSortCol}"
${sSortDir_0}
LIMIT #{iDisplayLength} OFFSET #{iDisplayStart}
